import { __decorate } from "tslib";
import { Injectable } from "@angular/core";
import { BehaviorSubject } from "rxjs";
import { environment } from "../../environments/environment";
let CommonService = class CommonService {
    constructor(route, http = null) {
        this.route = route;
        this.http = http;
        this.base64ImgSource = new BehaviorSubject("");
        this.base64ImgObservable = this.base64ImgSource.asObservable();
        this.typesSource = new BehaviorSubject([]);
        this.typesObservable = this.typesSource.asObservable();
        this.typeSource = new BehaviorSubject(null);
        this.typeObservable = this.typeSource.asObservable();
        this.create = async (type) => {
            return await this.http.post(this.apiUrl, type).toPromise();
        };
        this.update = async (type) => {
            return await this.http.patch(this.apiUrl, type).toPromise();
        };
        this.delete = async (id) => {
            return await this.http.delete(this.apiUrl + "/" + id).toPromise();
        };
        this.getAll = async () => {
            return await this.http.get(this.apiUrl).toPromise();
        };
        this.getById = async (id) => {
            return await this.http
                .get(this.apiUrl + "/GetById/" + id)
                .toPromise();
        };
        this.getByName = async (name) => {
            return await this.http
                .get(this.apiUrl + "/GetByName/" + name)
                .toPromise();
        };
        this.setType = async (type) => {
            this.typeSource.next(type);
        };
        this.setTypes = async (types) => {
            this.typesSource.next(types);
        };
        this.apiUrl = environment.apiRoot + route;
    }
};
CommonService = __decorate([
    Injectable({
        providedIn: "root",
    })
], CommonService);
export { CommonService };
