import { __decorate } from "tslib";
import { Component, EventEmitter, Input, Output } from '@angular/core';
import { CommonService } from '../../services/common.service';
let AreaFormComponent = class AreaFormComponent {
    constructor(router, http, modalService) {
        this.router = router;
        this.http = http;
        this.modalService = modalService;
        this.emitRefreshAreas = new EventEmitter();
        this.createOrUpdate = async (area) => {
            await (area["id"]
                ? this.areasService.update(area)
                : this.areasService.create(area)).then(() => {
                this.router.navigate(["areas"]);
                this.modalService.dismissAll();
                this.emitRefreshAreas.emit(true);
            });
        };
        this.areasService = new CommonService("areas", http);
    }
    ngOnInit() { }
    onSubmit(data) {
        this.createOrUpdate(data.value);
    }
};
