import { __decorate } from "tslib";
import { Component, EventEmitter, Input, Output } from "@angular/core";
import { CommonService } from "../services/common.service";
let AreaComponent = class AreaComponent {
    constructor(router, http, modalService) {
        this.router = router;
        this.http = http;
        this.modalService = modalService;
        this.collections = [];
        this.refreshAreas = new EventEmitter();
        this.areaEmitter = new EventEmitter();
        this.emitRefreshAreas = () => {
            this.refreshAreas.emit();
        };
        this.areasService = new CommonService("areas", http);
        this.collectionsService = new CommonService("collections", http);
    }
    async ngOnInit() {
        if (this.createArea) {
            this.isCreateOrEdit = true;
            this.areaId = null;
        }
        if (this.areaId) {
            this.isCreateOrEdit = true;
            this.area = await this.areasService.getById(this.areaId);
            this.areaEmitter.emit(this.area);
        }
    }
};
