import { __decorate } from "tslib";
import { Component } from '@angular/core';
import { CommonService } from '../../services/common.service';
let EventFormComponent = class EventFormComponent {
    constructor(router, http, modalService) {
        this.router = router;
        this.http = http;
        this.modalService = modalService;
        this.createOrUpdate = async (event) => {
            await (event["id"]
                ? this.eventsService.update(event)
                : this.eventsService.create(event)).then(() => {
                this.modalService.dismissAll();
                //this.refreshTimeline.emit(true);
            });
        };
        this.eventsService = new CommonService("events", http);
    }
    ngOnInit() {
    }
    onSubmit(data) {
        this.createOrUpdate(data.value);
    }
};
EventFormComponent = __decorate([
    Component({
        selector: 'app-event-form',
        templateUrl: './event-form.component.html',
        styleUrls: ['./event-form.component.css']
    })
], EventFormComponent);
export { EventFormComponent };
