import { CommonService } from "../services/common.service";
import * as i0 from "@angular/core";
import * as i1 from "@angular/router";
import * as i2 from "@angular/common/http";
export class AttributeComponent {
    constructor(router, http, route) {
        this.router = router;
        this.http = http;
        this.route = route;
        this.createOrUpdate = async (attribute) => {
            await (attribute["id"]
                ? this.attributesService.update(attribute)
                : this.attributesService.create(attribute)).then(() => {
                this.router.navigate(["attributes"]);
            });
        };
        this.attributesService = new CommonService("attributes", http);
    }
    async ngOnInit() {
        await this.route.paramMap.subscribe((params) => {
            this.id = params.get("id");
        });
        if (this.id) {
            this.attribute = await this.attributesService.getById(this.id);
        }
    }
    onSubmit(data) {
        this.createOrUpdate(data.value);
    }
}
